mapper.select:
select lodgingId, lodgingName, b.leaseId, c.cateCode, lodgingStock, a.regDate from reserve_lodging a left outer join reserve_lease b on a.leaseId = b.leaseId left outer join reserve_bcate c on a.cateCode = c.cateCode
where (lodgingName like concat ('%', #{keyword}, '%'))
order by lodgingId desc limit #{skip}, #{amount}


	select lodgingId, lodgingName, (select leaseId from reserve_lease where leaseId =reserve_lodging.leaseId) leaseName, 
		leaseId, cateCode, lodgingPrice, lodgingStock, lodgingIntro, lodgingContents, regDate, updateDate 
	from reserve_lodging where lodgingId = #{lodgingId}

mapper.update:

	update reserve_lodging set lodgingName = #{lodgingName}, leaseId = #{leaseId}, cateCode = ${cateCode}, 
	lodgingPrice = #{lodgingPrice}, lodgingStock = #{lodgingStock}, lodgingIntro = #{lodgingIntro}, lodgingContents = #{lodgingContents}, updateDate = now()
	where lodgingId = ${lodgingId}

mapper.select:
select reserveId, memberId, reserveState, reserveDate from reserve_reserve
where memberId like concat('%', #{keyword}, '%')
order by reserveDate desc limit #{skip}, #{amount}
